<?php  	//消息相关控制器	Class DownloadAction{        public function app(  )        {            //全部变成小写字母            if(strpos($_SERVER['HTTP_USER_AGENT'], 'iPhone')||strpos($_SERVER['HTTP_USER_AGENT'], 'iPad')){                $url = 'https://fir.im/JLKC1';                header('Location: ' . $url );            }else if(strpos($_SERVER['HTTP_USER_AGENT'], 'Android')){                $url = 'https://fir.im/JLKC';                header('Location: ' . $url );            }else{                alert('请使用安卓和iphone下载！');            }            die;                   }        public function is_app_currentsys(){            if(strpos($_SERVER['HTTP_USER_AGENT'], 'iPhone')||strpos($_SERVER['HTTP_USER_AGENT'], 'iPad')){                echo $dowmload_url = "ios";//判断为ios 苹果手机            }else if(strpos($_SERVER['HTTP_USER_AGENT'], 'Android')){                echo $dowmload_url = '安卓手机';//判断为安卓手机            }else if(strpos($_SERVER['HTTP_USER_AGENT'], 'MicroMessenger') !== false){                echo $dowmload_url = '微信打开的网页';//填写腾讯应用宝的链接地址            }else{                echo $dowmload_url = ' 无法判断';//pc或者说未判断出的情况            }        }    }