<?php  	//消息相关控制器	Class DownloadAction{        public function app(  )        {            //全部变成小写字母            if(strpos($_SERVER['HTTP_USER_AGENT'], 'iPhone')||strpos($_SERVER['HTTP_USER_AGENT'], 'iPad')){                $url = 'https://fir.im/JLKC';                header('Location: ' . $url );            }else if(strpos($_SERVER['HTTP_USER_AGENT'], 'Android')){                $url = 'https://fir.im/JLKC1';                header('Location: ' . $url );            }else{                alert('请使用安卓和iphone下载！');            }            die;                   }    }